    public static function dropdown($title, $items)
    {
        $title = $title ?? "<i title='". \Yii::t('common', 'Menu')."' class=\"mdi mdi-dots-vertical m-0 text-muted h5\"></i>";
        $items = implode('', array_map(function ($v) {
            return Html::a($v['label'], $v['url'], $v['options'] ?? ['class' => 'dropdown-item']);
        }, $items));
        return <<<HTML
<div class="dropdown">
    <a href="#" class="dropdown-toggle arrow-none" data-toggle="dropdown" aria-expanded="false"> {$title} </a>
    <div class="dropdown-menu"> {$items} </div>
</div>
HTML;

    }
